mapper.insert:

	SELECT REQ_SEQ.NEXTVAL AS reqNo FROM DUAL

INSERT INTO REQ_BOARD(
	  REQ_NO
    , EMP_CD
    , REQ_TTL
	, REQ_CN
	, REQ_DT
	, REQ_PW
	, REQ_CHECK
)VALUES(
	  #{reqNo}
	, #{empCd,jdbcType=VARCHAR}
	, #{reqTtl,jdbcType=VARCHAR}
	, #{reqCn,jdbcType=CLOB}
	,  SYSDATE
	, #{reqPw,jdbcType=NUMERIC}
	, 'N'
)

mapper.update:
UPDATE REQ_BOARD
SET
	REQ_TTL = #{reqTtl}
   ,REQ_CN = #{reqCn}
WHERE REQ_NO = #{reqNo}

update req_board
set req_check = 'Y'
where req_no = #{reqNo}
