mapper.insert:

    SELECT emp_cd
    FROM EMPLOYEE
    WHERE EMP_CD = (SELECT TO_CHAR(SYSDATE, 'YYMM') || LPAD(TO_NUMBER(MAX(SUBSTR(emp_cd, 5))), 3, '0') FROM EMPLOYEE)

