mapper.insert:

	    SELECT RB.REQ_NO
	    FROM REQ_BOARD RB
	    WHERE RB.REQ_NO = #{reqNo,jdbcType=NUMERIC}

    INSERT INTO REQ_CMNT
    (
        RCNMT_NO
        , REQ_NO
        , RCNMT_DT
        , RCMNT_CN
    )
    VALUES(
        REQ_CMNT_SEQ.NEXTVAL
        , #{reqNo,jdbcType=NUMERIC}
        , SYSDATE
        , #{rcmntCn,jdbcType=VARCHAR}
    )

mapper.update:
UPDATE REQ_CMNT 
SET 
RCNMT_DT = SYSDATE
, RCMNT_CN = #{rcmntCn}
WHERE RCNMT_NO = #{rcnmtNo}
