mapper.select:
SELECT RNUM, COMPL_NO, COMPL_TTL , COMPL_DT, COMPL_EMP_NAME , EMP_NAME, COMPL_EMP_CD, EMP_CD FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT COMPL_NO, COMPL_TTL, COMPL_DT , EM.EMP_NAME AS COMPL_EMP_NAME , E.EMP_NAME, C.EMP_CD AS COMPL_EMP_CD , C.EMP_CD AS EMP_CD
FROM COMPL_BOARD C LEFT OUTER JOIN EMPLOYEE E ON (E.EMP_CD = C.EMP_CD) LEFT OUTER JOIN EMPLOYEE EM ON (C.COMPL_EMP_CD = EM.EMP_CD) LEFT OUTER JOIN DEPARTMENT D ON (E.DEP_CD = D.DEP_CD AND C.COMPL_EMP_CD = E.EMP_CD) ORDER BY COMPL_DT DESC ) A
WHERE ROWNUM <= #{endRow} ) WHERE RNUM >= #{startRow}

