mapper.select:
SELECT B.* FROM( SELECT ROWNUM RNUM, A.* FROM( SELECT DOC.DOC_NO , DOC.EMP_CD , DOC.SIGNPATH_NO , DOC.DT_NO , DOC.DOC_TITLE , DOC.DOC_CONTENT , DOC.DOC_TYPE , DOC.DOC_UPDATEDATE , DOC.DOC_SIGN , DOC.CODE , DOC.ATCH_NO , EMP.EMP_NAME , EMP.DEP_CD , DEP.DEP_NAME FROM DOCUMENT DOC INNER JOIN EMPLOYEE EMP ON (DOC.EMP_CD = EMP.EMP_CD) INNER JOIN DEPARTMENT DEP ON(DEP.DEP_CD = EMP.DEP_CD) WHERE DOC.EMP_CD=#{detailCondition.empCd} AND CODE!='D_03'
AND CODE = #{detailCondition.code}
ORDER BY TO_NUMBER(DOC_NO) DESC )A )B
WHERE RNUM >=#{startRow} AND RNUM <=#{endRow}
